import React from "react";
import { Card } from "react-bootstrap";
import movieDetails from "../models/movieDetails";

type Props = {
    moviedetails : movieDetails
};

const MovieItem = ({moviedetails} : Props) => {
    return (
        <Card className="my-3" style={{ width: '14rem', height: '24rem'}}>
            {/*<Card.Img variant="top" src={`${process.env.REACT_APP_BASE_URL}${moviedetails.poster}`} />*/}
            <Card.Img variant="top" src={moviedetails.posterurl} style={{height: '16rem'}} />
            <Card.Body>
                <Card.Title style={{fontSize: '1rem', overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap"}}>{moviedetails.title}</Card.Title>
                <div className="text-center" style={{fontSize: '0.75rem'}}>
                    <button style={{backgroundColor: "transparent", border: "none", color: "black"}}>
                        Add to Favourites
                    </button>
                </div>
            </Card.Body>
        </Card>
    );
};

export default MovieItem;
