import { Component } from "react";
import { Alert, Col, Row } from "react-bootstrap";
import { RouteComponentProps } from "react-router-dom";
import movieDetails from "../models/movieDetails";
import getMovieList from "../services/GetMovieList";
import LoadingIndicator from "./common/LoadingIndicator";
import MovieItem from "./MovieItem";

type Props = {
    movieListTypePath : RouteComponentProps
};

type State = {
    status: "LOADING" | "LOADED" | "ERROR_LOADING",
    movies?: movieDetails[],
    error?: Error
};

class ListMovieItems extends Component<RouteComponentProps, State> {
    state : State = {
        status: 'LOADING'
    };

    

    render() {
        let el;

        switch(this.state.status) {
            case 'LOADING':
                el = (<LoadingIndicator size="large" message="Fetching movie list. Please Wait!"/>);
                break;
            case 'LOADED':
                el = (
                    <>
                        <h3 className="my-3">Movies</h3>
                        <Row xs={2} md={3} lg={5}>
                            {
                                this.state.movies?.map(
                                    movie => (
                                        <Col key={movie.id}>
                                            <MovieItem moviedetails={movie} />
                                        </Col>
                                    )
                                )
                            }
                        </Row>
                    </>
                );
                break;
            case 'ERROR_LOADING':
                el = (
                    <Alert variant="danger my-3">
                        {this.state.error?.message}
                    </Alert>
                );
                break;
        };

        return el;
    }

    async componentDidMount() {
        const {match} = this.props.movieListTypePath;
        console.log("ListMovieItem ComponentDidMount: ",match.path)
        this.setState({
            status: 'LOADING'
        });

        try {
            const data = await getMovieList(match.path);
            this.setState({
                status: 'LOADED',
                movies: data
            });
        } catch(error:any) {
            this.setState({
                status: 'ERROR_LOADING',
                error
            });
        }
    }
};

export default ListMovieItems;
