import movieDetails from "../models/movieDetails";

type Props = {
    movieDetails : movieDetails
}
const ShowMovieDetails = ({movieDetails} : Props) => {
    return (
        <>
            <Row>
                <Col xs={12}>
                    <h1>{name}</h1>
                    <hr />
                </Col>
                <Col xs={12} lg={4} className="my-2">
                    <img
                        src={`${process.env.REACT_APP_API_BASE_URL}${imageUrl}`}
                        alt={name}
                        className="w-100"
                    />
                </Col>
                <Col xs={12} lg={8} className="my-2">
                    <div>
                        {
                            cuisines.map(
                                cuisine => (
                                    <Badge bg="primary me-2" key={cuisine}>{cuisine}</Badge>
                                )
                            )
                        }
                    </div>
                    <div className="fs-5 my-2">
                        {description}
                    </div>
                    <Row xs={3} className="text-sm">
                        <Col>
                            <FontAwesomeIcon icon={faClock} className="me-1" />
                            <span>{opens} - {closes}</span>
                        </Col>
                        <Col>
                            <Rating value={rating} className="me-2" />
                            {rating} ({numRatings} ratings)
                        </Col>
                        <Col>
                            Cost for two: Rs. {costForTwo}
                        </Col>
                    </Row>
                </Col>
            </Row>  
        </>
    );
}

export default ShowMovieDetails;
